mapper.select:
SELECT AUTHRT_CD, AUTHRT_NM, USE_YN, REG_DT, RGTR_ID, MDFCN_DT, MDFR_ID FROM OPER.TB_CM_AUTHRT
AND AUTHRT_CD = #{authrtCd}
AND AUTHRT_NM LIKE CONCAT('%', #{authrtNm}, '%')
AND USE_YN = #{useYn}
ORDER BY AUTHRT_CD

mapper.update:
UPDATE OPER.TB_CM_AUTHRT
AUTHRT_NM = #{authrtNm}, USE_YN = #{useYn}, MDFCN_DT = #{mdfcnDt}, MDFR_ID = #{mdfrId},
WHERE AUTHRT_CD = #{authrtCd}

UPDATE OPER.TB_CM_AUTHRT
SET USE_YN   = 'N',
    MDFCN_DT = NOW(),
    MDFR_ID  = #{mdfrId}
WHERE AUTHRT_CD = #{authrtCd}

mapper.select:
SELECT COUNT(*)
FROM OPER.TB_CM_AUTHRT
WHERE AUTHRT_CD = #{authrtCd}

SELECT COUNT(*)
FROM OPER.TB_CM_AUTHRT
WHERE AUTHRT_CD = #{authrtCd}

SELECT AUTHRT_CD
FROM OPER.tb_cm_user_authrt
WHERE USER_ID = #{userId}
