mapper.select:
SELECT FILE_GROUP_ID
     , FILE_ACS_AUTHRT_CD
     , FILE_MDFCN_AUTHRT_CD
     , BFR_FILE_GROUP_ID
     , USE_YN
     , REG_DT
     , RGTR_ID
     , MDFCN_DT
     , MDFR_ID
FROM OPER.TB_CM_FILE_GROUP
WHERE FILE_GROUP_ID = #{fileGroupId}

    AND FILE_ACS_AUTHRT_CD = #{fileAcsAuthrtCd}


    AND FILE_MDFCN_AUTHRT_CD = #{fileMdfcnAuthrtCd}


    AND BFR_FILE_GROUP_ID = #{bfrFileGroupId}



        AND USE_YN = #{useYn}


        AND USE_YN = 'Y'

SELECT FILE_GROUP_ID , FILE_ID , FILE_PATH_NM , STRG_FILE_NM , ORGNL_FILE_NM , FILE_SZ , FILE_EXTN_NM , USE_YN , REG_DT , RGTR_ID , MDFCN_DT , MDFR_ID FROM OPER.TB_CM_FILE_DTL WHERE FILE_GROUP_ID = #{fileGroupId} AND FILE_ID = #{fileId}
AND FILE_PATH_NM LIKE CONCAT('%',#{filePathNm},'%')
AND STRG_FILE_NM LIKE CONCAT('%',#{strgFileNm},'%')
AND ORGNL_FILE_NM LIKE CONCAT('%',#{orgnlFileNm},'%')
AND USE_YN = #{useYn}
AND USE_YN = 'Y'

SELECT FILE_GROUP_ID , FILE_ID , FILE_PATH_NM , STRG_FILE_NM , ORGNL_FILE_NM , FILE_SZ , FILE_EXTN_NM , USE_YN , REG_DT , RGTR_ID , MDFCN_DT , MDFR_ID FROM OPER.TB_CM_FILE_DTL WHERE FILE_GROUP_ID = #{fileGroupId}
AND FILE_ID = #{fileId}
AND FILE_PATH_NM LIKE CONCAT('%',#{filePathNm},'%')
AND STRG_FILE_NM LIKE CONCAT('%',#{strgFileNm},'%')
AND ORGNL_FILE_NM LIKE CONCAT('%',#{orgnlFileNm},'%')
AND USE_YN = #{useYn}
AND USE_YN = 'Y'

mapper.insert:
INSERT INTO OPER.TB_CM_FILE_GROUP (
    FILE_GROUP_ID
    , FILE_ACS_AUTHRT_CD
    , FILE_MDFCN_AUTHRT_CD
    , BFR_FILE_GROUP_ID
    , USE_YN
    , REG_DT
    , RGTR_ID
    , MDFCN_DT
    , MDFR_ID
)
VALUES
(
    #{fileGroupId}
    , #{fileAcsAuthrtCd}
    , #{fileMdfcnAuthrtCd}
    , #{bfrFileGroupId}
    , 'Y'
    , NOW()
    , #{rgtrId}
    , NULL
    , NULL
)

mapper.update:
UPDATE OPER.TB_CM_FILE_GROUP
SET MDFCN_DT = NOW()
  , MDFR_ID = #{mdfrId}

        , FILE_ACS_AUTHRT_CD = #{fileAcsAuthrtCd}


        , FILE_MDFCN_AUTHRT_CD = #{fileMdfcnAuthrtCd}


        , BFR_FILE_GROUP_ID = #{bfrFileGroupId}


        , USE_YN = #{useYn}

WHERE FILE_GROUP_ID = #{fileGroupId}

mapper.insert:
